import React from 'react'

const Header = (props) => {
  console.log(props)
  return (
    <h1>
      {props.course}
    </h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part part={props.parts[0].name} excercise={props.parts[0].exercises}/>
      <Part part={props.parts[1].name} excercise={props.parts[1].exercises}/>
      <Part part={props.parts[2].name} excercise={props.parts[2].exercises}/>

    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises}</p>
    </div>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.part} {props.excercise}
    </p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
  {
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
]

  return (
    <div>
      <Header course={course}/>
      <Content parts={parts}/>
      <Total parts={parts}/> 

    </div> 
  )
}

export default App
