import React, { useState } from 'react'

const Button = ({clickHandler, text}) => <button onClick={clickHandler}>{text}</button>
const Display = ({text}) => <div>{text}</div>

const StatisticLine = ({text,value}) => {
  return (
    <div>{text}{value}</div>
  )
}

const Statistics = ({good, neutral, bad}) => {
  const total = good + bad + neutral 
  const average = (good - bad) / total
  const positive = good / total * 100
  if (total === 0) {
    return (
      <div>
        <p>No feedback given.</p>
      </div>
    )
  }
  return (
    <div>
    <h1>statistics</h1>
    <Display text={'good: ' + good}></Display>
    <Display text={'neutral: ' + neutral}></Display>
    <Display text={'bad: ' + bad}></Display>
    <StatisticLine text={'total'} value={total}/>
    <Display text={'average: ' + average}></Display>
    <Display text={'positive: ' + positive + ' %'}></Display>
    </div>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const rateGood = () => {
    setGood(good + 1)
  }

  const rateNeutral= () => {
    setNeutral(neutral + 1)
  }

  const rateBad = () => {
    setBad(bad + 1)
  }


  return (
    <div>
      <h1>give feedback</h1>
      <Button clickHandler={rateGood} text='good'></Button>
      <Button clickHandler={rateNeutral} text='neutral'></Button>
      <Button clickHandler={rateBad} text='bad'></Button>
      <Statistics good={good} bad={bad} neutral={neutral}/>
    </div>
  )
}

export default App
