import React, { useState } from 'react'

const Button = ({clickHandler, text}) => <button onClick={clickHandler}>{text}</button>
const Display = (props) => {
  return (
  <p>
    {props.text}
  </p>
  )
}
const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const rateGood = () => {
    setGood(good+1)
  }

  const rateNeutral= () => {
    setNeutral(neutral+1)
  }

  const rateBad = () => {
    setBad(bad+1)
  }

  return (
    <div>
      <h1>give feedback</h1>
      <Button clickHandler={rateGood} text='good'></Button>
      <Button clickHandler={rateNeutral} text='neutral'></Button>
      <Button clickHandler={rateBad} text='bad'></Button>

      <h1>statistics</h1>
      <Display text={'good: ' + good}></Display>
      <Display text={neutral}></Display>
      <Display text={bad}></Display>

    </div>
  )
}

export default App
